// actions.js
import { ADD_ACADEMIC_FORM, DELETE_ACADEMIC_FORM, UPDATE_ACADEMIC_FORM, UPDATE_FORM_FIELD, ADD_LIST_FORM, DELETE_LIST_FORM,UPDATE_LIST_FORM } from '../Redux/ActionTypes';

export const addAcademicForm = (form) => ({
  type: ADD_ACADEMIC_FORM,
  payload: form,
});

export const updateAcademicForm = (id, updatedForm) => ({
  type: UPDATE_ACADEMIC_FORM,
  payload: { id, updatedForm },
});

export const deleteAcademicForm = (id) => ({
  type: DELETE_ACADEMIC_FORM,
  payload: id,
});

export const updateFormField = (formName, fieldName, value) => ({
  type: UPDATE_FORM_FIELD,
  payload: { formName, fieldName, value },
});

export const addListForm = (formName,form) => ({
  type: ADD_LIST_FORM,
  payload: { formName, form }
});

export const updateListForm = (formName, id, updatedForm) => ({
  type: UPDATE_LIST_FORM,
  payload: { formName, id, updatedForm },
});

export const deleteListForm = (formName, id) => ({
  type: DELETE_LIST_FORM,
  payload: { formName, id },
});
