// src/components/PersonalInfoForm.js
import React, { useState, useEffect } from 'react';
import { countries } from 'countries-list';
import './PersonalInfoForm.css';

const PersonalInfoForm = () => {
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    phoneNumber: '',
    countryCode: '+1',
    street: '',
    city: '',
    state: '',
    pin: '',
    country: 'United States',
    photo: null, // New field for photo
  });

  const [countryNames, setCountryNames] = useState([]);
  const [countryCodes, setCountryCodes] = useState([]);

  useEffect(() => {
    // Extract country names and codes
    const countryList = Object.entries(countries);
    const names = countryList.map(([code, details]) => details.name);
    const codes = countryList.map(([code, details]) => `+${details.phone} - ${details.name}`);

    setCountryNames(names);
    setCountryCodes(codes);
  }, []);

  const handleChange = (e) => {
    const { name, value, type } = e.target;

    // Handle file input separately
    if (type === 'file') {
      setFormData({
        ...formData,
        [name]: e.target.files[0],
      });
    } else {
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Data:', formData);
    // You can add form submission logic here
  };

  return (
    <div className='container'>
      <form onSubmit={handleSubmit} className='form-style'>
        <h1>Personal Information</h1>
        <label className='label-style'>
          Full Name:
          <input
            type="text"
            name="fullName"
            value={formData.fullName}
            onChange={handleChange}
            className='input-style'
            placeholder='Full Name'
          />
        </label>
        <br />
        <label className='label-style'>
          Email:
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className='input-style'
            placeholder='Email'
          />
        </label>
        <br />
        <label className='label-style'>
          Photo:
          <input
            type="file"
            name="photo"
            accept="image/*"
            onChange={handleChange}
            className='input-style'
          />
        </label>
        <br />
        <label className='label-style'>
            Phone Number:
            <div className='phone-container'>
              <select
                name="countryCode"
                value={formData.countryCode}
                onChange={handleChange}
                className='select-style'
                placeholder='+CC'
              >
                {countryCodes.map((code, index) => (
                  <option key={index} value={code}>
                    {code}
                  </option>
                ))}
              </select>
              <input
                type="text"
                name="phoneNumber"
                value={formData.phoneNumber}
                onChange={handleChange}
                className='input-style'
                placeholder='Phone Number'
              />
            </div>
          </label>
          <br />
          <label className='label-style'>
            Address(line 1):
            <input
              type="text"
              name="street"
              value={formData.street}
              onChange={handleChange}
              className='input-style'
              placeholder='Street/Locality'
            />
          </label>
          <br />
          <label className='label-style'>
            Address(line 2):
            <input
              type="text"
              name="city"
              value={formData.city}
              onChange={handleChange}
              className='input-style'
              placeholder='City/Town/Village'
            />
          </label>
          <br />
          <div className='address-container'>
            <label className='state-label-style'>
              State:
              <input
                type="text"
                name="state"
                value={formData.state}
                onChange={handleChange}
                className='input-style'
                placeholder='State'
              />
            </label>
            <label className='label-style'>
              PIN:
              <input
                type="text"
                name="pin"
                value={formData.pin}
                onChange={handleChange}
                className='input-style'
                placeholder='PIN Code'
              />
            </label>
          </div>
          <br />
          <label className='label-style'>
            Country:
            <select name="country" value={formData.country} onChange={handleChange} className='select-style' placeholder='Country'>
              {countryNames.map((name, index) => (
                <option key={index} value={name}>
                  {name}
                </option>
              ))}
            </select>
          </label>
          <br />
        <button type="submit" className='button-style'>Submit</button>
      </form>
    </div>
  );
};

export default PersonalInfoForm;
