import React from 'react';
import { useForm } from '../Redux/FormContext'; // Update the path accordingly
import { FaTrash } from 'react-icons/fa'; // Import the trash icon or any other delete icon you prefer

const AnotherPageComponent = () => {
  const { formData, updateFormData } = useForm();

  // Check if formData and AcademicForm are defined
  if (!formData || !formData.AcademicForm) {
    return null; // or handle the case where formData or AcademicForm is not defined
  }

  const addAcademicForm = () => {
    const newAcademicForm = {
      id: Date.now(),
      degree: '',
      course: '',
      institution: '',
      startYear: '',
      endYear: '',
      gpaScored: '',
      gpaOutOf: '',
    };

    updateFormData('AcademicForm', [...formData.AcademicForm, newAcademicForm]);
  };

  const deleteAcademicForm = (id) => {
    const updatedAcademicForms = formData.AcademicForm.filter(form => form.id !== id);
    updateFormData('AcademicForm', updatedAcademicForms);
  };

  return (
    <div>
      <h2>Academic Form Data:</h2>
      <button onClick={addAcademicForm}>Add Academic Form</button>
      <ul>
        {formData.AcademicForm.map((academicForm, index) => (
          <li key={academicForm.id}>
            {/* Render each field of the academic form as needed */}
            <p>Degree: {academicForm.degree}</p>
            <p>Course: {academicForm.course}</p>
            {/* Add other fields here... */}
            <button onClick={() => deleteAcademicForm(academicForm.id)}>
              <FaTrash />
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AnotherPageComponent;
