// actions.js

export const ADD_DATA = 'ADD_DATA';
export const DELETE_DATA = 'DELETE_DATA';
export const UPDATE_DATA = 'UPDATE_DATA';

export const addData = (type, data) => ({
  type: ADD_DATA,
  payload: { type, data },
});

export const deleteData = (type, id) => ({
  type: DELETE_DATA,
  payload: { type, id },
});

export const updateData = (type, data) => ({
  type: UPDATE_DATA,
  payload: { type, data },
});
