// academicFormsReducer.js
import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  academicForms: [{ id: 1, degree: '', course: '', institution: '', startYear: '', endYear: '', gpaScored: '', gpaOutOf: '' }],
};

const academicFormsSlice = createSlice({
  name: 'academicForms',
  initialState,
  reducers: {
    addAcademicForm: (state, action) => {
      // Handle adding a new academic form
      state.academicForms.push(action.payload);
    },
    updateAcademicForm: (state, action) => {
      // Handle updating an academic form
      const { id, updatedForm } = action.payload;
      const formIndex = state.academicForms.findIndex(form => form.id === id);
      if (formIndex !== -1) {
        state.academicForms[formIndex] = { ...state.academicForms[formIndex], ...updatedForm };
      }
    },
    deleteAcademicForm: (state, action) => {
      // Handle deleting an academic form
      const idToDelete = action.payload;
      state.academicForms = state.academicForms.filter(form => form.id !== idToDelete);
    },
  },
});

export const { addAcademicForm, updateAcademicForm, deleteAcademicForm } = academicFormsSlice.actions;
export default academicFormsSlice.reducer;
