import React,{ useState, useEffect } from 'react';
import Select from "react-select";

const SkillsForm = () => {
    const [ searchTerm, setSearchTerm ] = useState('');
    const [ options, setOptions ] = useState([]);
    const [selectedOption, setSelectedOption] = useState(null);
    const [inputValue, setInputValue] = useState('');

    const getSkills = (characters) => {
        fetch('http://127.0.0.1:8000/apis/user_skills/',{
            method:'POST',
            headers:{
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({"characters":characters})
        })
        .then(response => {
            if (!response.ok){
                throw new Error("Error in gettin data")
            }
            return response.json()
        })
        .then(data => setOptions(data['results']))
        .catch(error => console.log(error))
    }

    useEffect(() => {
        if (searchTerm.length >= 3){
            getSkills(searchTerm)
        } else{
            setOptions([])
        }
    },[searchTerm])

    const handleInputChange = (newValue) => {
        // Implement your search logic here (if needed)
        setInputValue(newValue);
      }

    return(
        <div>
            <Select
                value={selectedOption}
                onChange={(selected) => setSelectedOption(selected)}
                options={options}
                onInputChange={handleInputChange}
                inputValue={inputValue}
                placeholder="Skills..."
                isSearchable
            />
        </div>
    )
}

export default SkillsForm;
