import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useForm } from '../Redux/FormContext';

import { GrAdd } from 'react-icons/gr';
import { FaTrash } from 'react-icons/fa';

import './AcademicForm.css';

const AcademicForm = () => {
  const { formData = { academicForms: [] }, updateFormData } = useForm();
  const navigate = useNavigate();

  const addAcademicForm = () => {
    const newForm = {
      id: Date.now(),
      degree: '',
      course: '',
      institution: '',
      startYear: '',
      endYear: '',
      gpaScored: '',
      gpaOutOf: '',
    };

    updateFormData('AcademicForm', [...formData.academicForms, newForm]);
  };

  const deleteAcademicForm = (id) => {
    if (formData.academicForms.length > 1) {
      const updatedForms = formData.academicForms.filter((form) => form.id !== id);
      updateFormData('AcademicForm', updatedForms);
    }
  };

  const handleInputChange = (id, event) => {
    const { name, value } = event.target;
    const updatedForms = formData.academicForms.map((form) =>
      form.id === id ? { ...form, [name]: value } : form
    );

    updateFormData('AcademicForm', updatedForms);
  };

  const print = () => {
    console.log(formData);
  };

  return (
    <div className="container">
      <div className="form-style">
        <h2 className="p-3 mb-4 d-flex justify-content-center">Academic Details</h2>
        {formData.academicForms.map((form, index) => (
          <div key={form.id}>
            <label className="label-style mt-5">
              Institution:
              <input
                type="text"
                name="institution"
                value={form.institution}
                onChange={(e) => handleInputChange(form.id, e)}
                className="input-style p-2"
                placeholder="College/University/School"
              />
            </label>
            <br />
            {/* ... rest of your form fields ... */}
            <div className="d-grid gap-2 d-md-flex justify-content-md-center" role="group" aria-label="Basic outlined example">
              <button onClick={addAcademicForm} className="add-more-academics">
                <GrAdd />
              </button>
              {index > 0 && (
                <button onClick={() => deleteAcademicForm(form.id)} className="remove-academics">
                  <FaTrash />
                </button>
              )}
            </div>
            <hr style={{ border: '1px solid #ccc', margin: '20px 0' }} />
          </div>
        ))}
        <div className="row">
          <div className="col-auto">
            <button className="btn description-button-style" onClick={() => navigate('/DescriptionInfo')}>
              Previous
            </button>
          </div>
          <div className="col text-end">
            <button className="btn description-button-style" onClick={print}>
              Next
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AcademicForm;
