import React,{ useState, useEffect } from 'react';
import Select from "react-select";

const SkillsForm = () => {
    const [ searchTerm, setSearchTerm ] = useState('')
    const [ options, setOptions ] = useState([])

    const getSkills = (characters) => {
        console.log("inside the getskills")
        fetch('http://127.0.0.1:8000/apis/user_skills/',{
            method:'POST',
            headers:{
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({"characters":characters})
        })
        .then(response => {
            if (!response.ok){
                throw new Error("Error in gettin data")
            }
            return response.json()
        })
        .then(data => {setOptions(data['results']);console.log(data['results'])})
        .catch(error => console.log(error))
    }

    useEffect(() => {
        if (searchTerm.length >= 3){
            console.log("the length is more than 3")
            getSkills(searchTerm)
        } else{
            setOptions([])
        }
    },[searchTerm])

    return(
        <div>
            <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
            />
            <Select
                options={options}
                // Customize as needed
            />
        </div>
    )
}

export default SkillsForm;
