// store.js
import { configureStore } from '@reduxjs/toolkit';
import FormsReducer from './Reducers/AcademicReducer';
import genericReducer from './Reducers/genericReducers';

const store = configureStore({
  reducer: {
    academicForms: FormsReducer,
    genericData: genericReducer, 
  },
});

export default store;
