import React, { useState, useEffect } from 'react';
import { countries } from 'countries-list';
import './PersonalInfoForm.css';

const PersonalInfoForm = () => {
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    phoneNumber: '',
    countryCode: '+1',
    address: '',
    country: 'United States',
  });

  const [countryNames, setCountryNames] = useState([]);
  const [countryCodes, setCountryCodes] = useState([]);

  useEffect(() => {
    // Extract country names and codes
    const countryList = Object.entries(countries);
    const names = countryList.map(([code, details]) => details.name);
    const codes = countryList.map(([code, details]) => `+${details.phone}`);

    setCountryNames(names);
    setCountryCodes(codes);
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Data:', formData);
  };

  return (
    <form onSubmit={handleSubmit} className='form-style'>
      <label className='label-style'>
        Full Name:
        <input
          type="text"
          name="fullName"
          value={formData.fullName}
          onChange={handleChange}
          className='input-style'
        />
      </label>
      <br />
      <label className='label-style'>
        Email:
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          className='input-style'
        />
      </label>
      <br />
      <label className='label-style'>
        Phone Number:
        <select
          name="countryCode"
          value={formData.countryCode}
          onChange={handleChange}
          className='select-style'
        >
          {countryCodes.map((code, index) => (
            <option key={index} value={code}>
              {code}
            </option>
          ))}
        </select>
        <input
          type="text"
          name="phoneNumber"
          value={formData.phoneNumber}
          onChange={handleChange}
          className='input-style'
        />
      </label>
      <br />
      <label className='label-style'>
        Address:
        <textarea
          name="address"
          value={formData.address}
          onChange={handleChange}
          className='textarea-style'
        />
      </label>
      <br />
      <label className='label-style'>
        Country:
        <select name="country" value={formData.country} onChange={handleChange} className='select-style'>
          {countryNames.map((name, index) => (
            <option key={index} value={name}>
              {name}
            </option>
          ))}
        </select>
      </label>
      <br />
      <button type="submit" className='button-style'>Submit</button>
    </form>
  );
};

export default PersonalInfoForm;
