import React,{ useState, useEffect } from 'react';
import Select from "react-select";
import CreatableSelect from 'react-select/creatable';

import skillsListConf from "../../Static/Configurations/skillsListConf";
import skillsList from '../../Static/Configurations/skillsListConf';

const SkillsForm = () => {
    const [ searchTerm, setSearchTerm ] = useState('')
    const [ options, setOptions ] = useState([])
    const [ selectedOptions, setSelectedOptions] = useState([]);

    useEffect(() => {
        console.log(selectedOptions);
        if (searchTerm.length >= 3){
            const filteredList = skillsList.filter(item => item.label.toLocaleLowerCase().includes(searchTerm.toLocaleLowerCase()));
            const sortedOptions = filteredList.sort((a, b) => a.label.localeCompare(b.label));
            setOptions(sortedOptions);
        } else{
            setOptions([]);
        }
    },[searchTerm])

    const handleInputChange = (newValue) => {
        setSearchTerm(newValue);
    }

    const handleSelectedValue = (e) => {
        const value = e;
        setSelectedOptions(value)
    }

    return(
        <div>
            <CreatableSelect isMulti isClearable options={options} onChange={handleSelectedValue} onInputChange={handleInputChange} inputValue={searchTerm} placeholder='Please type atleast 3 letters' />;
        </div>
    )
}

export default SkillsForm;
