// FormContext.js
import React, { createContext, useContext, useReducer } from 'react';

const FormContext = createContext();

export const useForm = () => {
  return useContext(FormContext);
};

export const FormProvider = ({ children }) => {
  const initialState = {
    // Initialize with default form values
    //PersonalInfoForm: { firstName: '', lastName: '', email: '', confirmEmail: '', street: '', city: '', state: '', pin: '', country: 'United States', photo: null},
    DescriptionForm: { introduction:'', unique: '', profession: '' },
  };

  const formReducer = (state, action) => {
    switch (action.type) {
      case 'UPDATE_PAGE':
        return {
          ...state,
          [action.page]: { ...state[action.page], ...action.data },
        };
      default:
        return state;
    }
  };

  const [formData, dispatch] = useReducer(formReducer, initialState);

  const updateFormData = (page, data) => {
    dispatch({ type: 'UPDATE_PAGE', page, data });
  };

  return (
    <FormContext.Provider value={{ formData, updateFormData }}>
      {children}
    </FormContext.Provider>
  );
};
