// src/components/PersonalInfoForm.js
import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { countries } from 'countries-list';

import { useForm } from '../Redux/FormContext';
import './PersonalInfoForm.css';

const PersonalInfoForm = () => {
  const navigate = useNavigate();
  const { formData, updateFormData } = useForm();
  const fileInputRef = useRef(null);

  const [countryNames, setCountryNames] = useState([]);

  const handleButtonClick = () => {
    // Trigger click on the hidden file input
    fileInputRef.current.click();
  };

  useEffect(() => {
    // Extract country names and codes
    const countryList = Object.entries(countries);
    const names = countryList.map(([code, details]) => details.name);

    setCountryNames(names);
  }, []);

  const handleChange = (e) => {
    const { name, value, type } = e.target;

    // Handle file input separately
    if (type === 'file') {
      updateFormData('PersonalInfoForm',{ [name]: e.target.files[0] });
    } else {
      updateFormData('PersonalInfoForm', { [name]: value });
    }
  };

  const handleSubmit = (e) => {
    console.log(formData);
    e.preventDefault();

    // You can add form submission logic here

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const validEmail = emailRegex.test(formData.email);
    const validConfirmEmail = emailRegex.test(formData.confirmEmail);

    if ((validEmail === false) || (validConfirmEmail === false)){ 
      alert('Please enter a valid email');
      return;
    } 

    if (formData.email !== formData.confirmEmail){
      alert("Email id don't match");
      return;
    }

    fetch('http://localhost:8000/User/PersonalInfo/',{
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        // Add any other headers as needed
      },
      body: JSON.stringify(formData),
    })
    .then(response => response.json())
    .then(data => {
      console.log('Backend response:', data);
    })
    .catch(error => {
      console.log("Error sending data to backend:", error);
    })
  };

  return (
    <div className='container'>
      <div className='form-style'>
        <h1>Personal Information</h1>
        <div className="input-group">
          <div className="input-group-item">
            <label className='label-style'>
              First Name:
              <input
                type="text"
                name="firstName"
                value={formData.PersonalInfoForm.firstName}
                onChange={handleChange}
                className='input-style'
                placeholder='First Name'
                required
              />
            </label>
          </div>
          <div className="input-group-item">
            <label className='label-style'>
              Last Name:
              <input
                type="text"
                name="lastName"
                value={formData.PersonalInfoForm.lastName}
                onChange={handleChange}
                className='input-style'
                placeholder='Last Name'
              />
            </label>
          </div>
        </div>
        <br />
        <div className="photo-upload">
          <label className='photo-label-style'>
            Photo:
            <input
              type="file"
              ref={fileInputRef}
              name="photo"
              //value={formData.PersonalInfoForm.photo}
              accept="image/*"
              onChange={handleChange}
              className='input-style'
              style={{display: 'none'}}
            />
            <div>
              <button className="custom-file-button" onClick={handleButtonClick}>
                Choose File
              </button>
              {!formData.PersonalInfoForm.photo && <p className='file-info'>No file chosen</p>}
              {formData.PersonalInfoForm.photo && <p className='file-info'>Selected file: {formData.PersonalInfoForm.photo.name}</p>}
            </div>
          </label>
        </div>
        <br />
        <div className='input-group'>
          <div className='input-group-item'>
            <label className='form-label label-style'>
              Email:
              <input
                type="email"
                name="email"
                value={formData.PersonalInfoForm.email}
                onChange={handleChange}
                className='input-style'
                placeholder='Email'
                required
              />
            </label>
          </div>
          <div className="input-group-item">
            <label className='label-style'>
                Confirm Email:
                <input
                  type="confirmEmail"
                  name="confirmEmail"
                  value={formData.PersonalInfoForm.confirmEmail}
                  onChange={handleChange}
                  className='input-style'
                  placeholder='Confirm Email'
                  required
                />
              </label>
          </div>
        </div>
          <br />
          <label className='label-style'>
            Address(line 1):
            <input
              type="text"
              name="street"
              value={formData.PersonalInfoForm.street}
              onChange={handleChange}
              className='input-style'
              placeholder='Street/Locality'
            />
          </label>
          <br />
          <label className='label-style'>
            Address(line 2):
            <input
              type="text"
              name="city"
              value={formData.PersonalInfoForm.city}
              onChange={handleChange}
              className='input-style'
              placeholder='City/Town/Village'
            />
          </label>
          <br />
          <div className='input-group'>
            <div className='input-group-item'>
              <label className='label-style'>
                State:
                <input
                  type="text"
                  name="state"
                  value={formData.PersonalInfoForm.state}
                  onChange={handleChange}
                  className='input-style'
                  placeholder='State'
                />
              </label>
            </div>
            <div className='input-group-item'>
              <label className='label-style'>
                PIN:
                <input
                  type="text"
                  name="pin"
                  value={formData.PersonalInfoForm.pin}
                  onChange={handleChange}
                  className='input-style'
                  placeholder='PIN Code'
                  required
                />
              </label>
            </div>
          </div>
          <br />
          <label className='label-style'>
            Country:
            <select name="country" onChange={handleChange} className='select-style' placeholder='Country'>
              {countryNames.map((name, index) => (
                <option key={index} value={name}>
                  {name}
                </option>
              ))}
            </select>
          </label>
          <br />
          <div className='d-flex justify-content-center'>
          <button type="submit" className='button-style' onClick={() => navigate('/DescriptionInfo')}>Next</button>
          </div>
      </div>
    </div>
  );
};

export default PersonalInfoForm;
