// reducer.js

import { ADD_DATA, DELETE_DATA, UPDATE_DATA } from './actions';

const initialState = {
  academicForms: [],
  // Add other dictionaries here
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_DATA:
      return {
        ...state,
        [action.payload.type]: [...state[action.payload.type], action.payload.data],
      };

    case DELETE_DATA:
      return {
        ...state,
        [action.payload.type]: state[action.payload.type].filter(item => item.id !== action.payload.id),
      };

    case UPDATE_DATA:
      const updatedData = state[action.payload.type].map(item =>
        item.id === action.payload.data.id ? action.payload.data : item
      );

      return {
        ...state,
        [action.payload.type]: updatedData,
      };

    default:
      return state;
  }
};

export default reducer;
