import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

import "./DescriptionForm.css";
import { useForm } from '../Redux/FormContext';

import salaried from "../../Static/salary.png";
import selfEmployed from "../../Static/computer-worker.png";
import student from "../../Static/graduated.png";
import freelancer from "../../Static/freelance.png";


const DescriptionForm = () => {
    const navigate = useNavigate();
    const location = useLocation();
    const [ formData, setFormData ] = useForm();

    const [ card, setCard] = useState('');

    const lines = [
        'I bring positivity wherever I go...',
        'I can take responsibilities for a task...',
        'I am batman...',
        'I am dead inside..',

        // Add more lines as needed
      ];

      const [currentLineIndex, setCurrentLineIndex] = useState(0);
      const [currentText, setCurrentText] = useState('');

      useEffect(() => {
        let lineIndex = 0;
        let textIndex = 0;

        const typeEffect = setInterval(() => {
          setCurrentText((prevText) => {
            const currentLine = lines[lineIndex];
            const updatedText = currentLine.substring(0, textIndex + 1);

            if (textIndex === currentLine.length) {
              // If the entire line is typed, move to the next line
              lineIndex = (lineIndex + 1) % lines.length;
              textIndex = 0;
            } else {
              textIndex += 1;
            }

            return updatedText;
          });
        }, 150); // Adjust the typing speed as needed

        return () => clearInterval(typeEffect);
      }, []);

    const handleChange = (e) => {
        const { name, value, type } = e.target;

        setFormData('descriptionInfo', { [name]: value });
    };

    const handleCardClick = (profession) => {
        setCard(profession);
        setFormData('descriptionInfo', { ['profession']: profession });
    };

    useEffect(() => {
        console.log(formData);
    },[formData])

    return(
        <div className='container text-center description-box'>
            <h2 className='p-3 mb-4 d-flex justify-content-center'>Description</h2>
            <div className='row justify-content-md-start'>
                <div class="mb-5">
                    <label for="exampleFormControlTextarea1" class="form-label d-flex justify-content-center">Tell us briefly about yourself</label>
                    <textarea class="form-control" name='introduction' id="exampleFormControlTextarea1" rows="3" placeholder='Introduce yourself...' onChange={handleChange} required></textarea>
                </div>
                <div class="mb-5">
                    <label for="exampleFormControlInput1" class="form-label d-flex justify-content-center">One characteristic which makes you special.</label>
                    <input type="email" class="form-control" name='unique' id="exampleFormControlInput1" placeholder={currentText} onChange={handleChange} required/>
                </div>
            </div>
            <div>
                <label className='d-flex justify-content-center'>Are you salaried, self-employed or a student?</label>
                <div className='row row-cols-sm-2 row-cols-md-4 ms-3 me-3 mb-5'>
                    <div className='col p-5'>
                        <div className={`${(card === 'salaried')?'description-card-highlight':'description-card'}`} name='salaried' onClick={() => handleCardClick('salaried')}>
                            <img src={salaried} alt="salaried" className="img-fluid mx-auto d-block p-2" style={{height:'84px',width:'84px'}}/>
                            <label className='d-flex justify-content-center'>Salaried</label>
                        </div>
                    </div>
                    <div className='col p-5'>
                        <div className={`${(card === 'selfEmployed')?'description-card-highlight':'description-card'}`} name='selfEmployed' onClick={() => handleCardClick('selfEmployed')}>
                            <img src={selfEmployed} alt="salaried" className="img-fluid mx-auto d-block p-2" style={{height:'84px',width:'84px'}}/>
                            <label className='d-flex justify-content-center'>Self-Employed</label>
                        </div>
                    </div>
                    <div className='col p-5'>
                        <div className={`${(card === 'freelancer')?'description-card-highlight':'description-card'}`} name='freelancer' onClick={() => handleCardClick('freelancer')}>
                            <img src={freelancer} alt="salaried" className="img-fluid mx-auto d-block p-2" style={{height:'84px',width:'84px'}}/>
                            <label className='d-flex justify-content-center'>Freelancer</label>
                        </div>
                    </div>
                    <div className='col p-5'>
                        <div className={`${(card === 'student')?'description-card-highlight':'description-card'}`} name='student' onClick={() => handleCardClick('student')}>
                            <img src={student} alt="salaried" className="img-fluid mx-auto d-block p-2" style={{height:'84px',width:'84px'}}/>
                            <label className='d-flex justify-content-center'>Student</label>
                        </div>
                    </div>
                </div>
            </div>
            <div className="row">
                <div className="col-auto">
                    <button className="btn description-button-style" onClick={() => navigate('/')}>Previous</button>
                </div>
                <div className="col text-end">
                    <button className="btn description-button-style">Next</button>
                </div>
            </div>
        </div>
    )
}

export default DescriptionForm;
