import React, { useState } from 'react';

const AcademicForm = () => {
  const [academicForms, setAcademicForms] = useState([
    { course: '', institution: '', year: '' },
  ]);

  const Test = () => {
    setAcademicForms([...academicForms, { course: '', institution: '', year: '' }]);
  };

  const handleInputChange = (index, event) => {
    const { name, value } = event.target;
    const updatedForms = [...academicForms];
    updatedForms[index][name] = value;
    setAcademicForms(updatedForms);
  };

  return (
    <div>
      {academicForms.map((form, index) => (
        <div key={index}>
          <label>
            Course:
            <input
              type="text"
              name="course"
              value={form.course}
              onChange={(e) => handleInputChange(index, e)}
            />
          </label>
          <label>
            Institution:
            <input
              type="text"
              name="institution"
              value={form.institution}
              onChange={(e) => handleInputChange(index, e)}
            />
          </label>
          <label>
            Year:
            <input
              type="text"
              name="year"
              value={form.year}
              onChange={(e) => handleInputChange(index, e)}
            />
          </label>
        </div>
      ))}
      <button onClick={addAcademicForm}>Add Academic Form</button>
    </div>
  );
};

export default Test;
