// AcademicFormsComponent.js

import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addData, deleteData, updateData } from './actions';

const Test = () => {
  const dispatch = useDispatch();
  const academicForms = useSelector(state => state.academicForms);

  const [selectedForm, setSelectedForm] = useState(null);

  const handleAddForm = () => {
    const newForm = { id: Date.now(), degree: '', course: '', institution: '', startYear: '', endYear: '', gpaScored: '', gpaOutOf: '' };
    dispatch(addData('academicForms', newForm));
  };

  const handleDeleteForm = (formId) => {
    dispatch(deleteData('academicForms', formId));
  };

  const handleUpdateForm = () => {
    if (selectedForm) {
      dispatch(updateData('academicForms', selectedForm));
      setSelectedForm(null);
    }
  };

  const handleFormSelection = (form) => {
    setSelectedForm(form);
  };

  return (
    <div>
      <h2>Academic Forms</h2>
      <ul>
        {academicForms.map(form => (
          <li key={form.id}>
            {form.degree} - {form.course}
            <button onClick={() => handleDeleteForm(form.id)}>Delete</button>
            <button onClick={() => handleFormSelection(form)}>Edit</button>
          </li>
        ))}
      </ul>

      <h2>{selectedForm ? 'Edit Form' : 'Add Form'}</h2>
      <form>
        <label>
          Degree:
          <input type="text" value={selectedForm?.degree || ''} onChange={(e) => setSelectedForm({ ...selectedForm, degree: e.target.value })} />
        </label>
        <label>
          Course:
          <input type="text" value={selectedForm?.course || ''} onChange={(e) => setSelectedForm({ ...selectedForm, course: e.target.value })} />
        </label>
        {/* Add other form fields here */}
        <button type="button" onClick={selectedForm ? handleUpdateForm : handleAddForm}>
          {selectedForm ? 'Update Form' : 'Add Form'}
        </button>
      </form>
    </div>
  );
};

export default Test;
