import React, { useState, useEffect } from 'react';
import Select from 'react-select';

import './SkillsForm.css';
import skillsList from "../../Static/Configurations/skillsList.conf";

const SkillsForm = () => {
  const [options, setOptions] = useState([]);
  const [selectedOption, setSelectedOption] = useState(null);
  const [inputValue, setInputValue] = useState('');

  // Simulating fetching data (replace with your API call)
  useEffect(() => {
    // Fetch your data and format it into options array
    const fetchData = async () => {
      // Example data (replace with actual fetched data)
      const fetchedData = [
        { value: 'option1', label: 'Option 1' },
        { value: 'option2', label: 'Option 2' },
        { value: 'option3', label: 'Option 3' },
        // Add more options as needed
      ];
      console.log(skillsList);
      setOptions(fetchedData);
    };

    fetchData();
  }, []);

  const handleInputChange = (newValue) => {
    // Implement your search logic here (if needed)
    setInputValue(newValue);
  };

  return (
    <div className="search-bar-container">
      <Select
        value={selectedOption}
        onChange={(selected) => setSelectedOption(selected)}
        options={options}
        onInputChange={handleInputChange}
        inputValue={inputValue}
        placeholder="Skills..."
        isSearchable
      />
    </div>
  );
};

export default SkillsForm;
