import React, { useState } from 'react';

const AcademicForm = () => {
    const [academicForms, setAcademicForms] = useState([
        { course: '', institution: '', year: '' },
      ]);

      const addAcademicForm = () => {
        setAcademicForms([...academicForms, { degree:'', course: '', institution: '', startYear: '', endYear:'', gpaScored:'', gpaOutOf:'' }]);
        console.log(academicForms);
      };

      const handleInputChange = (index, event) => {
        const { name, value } = event.target;
        const updatedForms = [...academicForms];
        updatedForms[index][name] = value;
        setAcademicForms(updatedForms);
      };

    return(
        <div>
            <div>
            {academicForms.map((form, index) => (
                <div key={index}>
                    <label>
                    Institution:
                    <input
                    type="text"
                    name="institution"
                    value={form.institution}
                    onChange={(e) => handleInputChange(index, e)}
                    />
                </label>
                <br />
                <label>
                    Degree:
                    <input
                    type="text"
                    name="degree"
                    value={form.degree}
                    onChange={(e) => handleInputChange(index, e)}
                    />
                </label>
                <label>
                    Course:
                    <input
                    type="text"
                    name="course"
                    value={form.course}
                    onChange={(e) => handleInputChange(index, e)}
                    />
                </label>
                <br />
                <label>
                    Start year:
                    <input
                    type="text"
                    name="startYear"
                    value={form.startYear}
                    onChange={(e) => handleInputChange(index, e)}
                    />
                </label>
                <label>
                    End year:
                    <input
                    type="text"
                    name="endYear"
                    value={form.endYear}
                    onChange={(e) => handleInputChange(index, e)}
                    />
                </label>
                <br />
                <label>
                    GPA:
                    <input
                    type="text"
                    name="gpaScored"
                    value={form.gpaScored}
                    onChange={(e) => handleInputChange(index, e)}
                    />
                </label>
                <label>
                    Out of:
                    <input
                    type="text"
                    name="gpaOutOf"
                    value={form.gpaOutOf}
                    onChange={(e) => handleInputChange(index, e)}
                    />
                </label>
                </div>
            ))}
            <button onClick={addAcademicForm}>Add Academic Form</button>
            </div>
        </div>
    )
}

export default AcademicForm;
