// src/components/PersonalInfoForm.js
import React, { useState, useEffect } from 'react';
import { countries } from 'countries-list';
import './PersonalInfoForm.css';

const PersonalInfoForm = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    confirmEmail: '',
    street: '',
    city: '',
    state: '',
    pin: '',
    country: 'United States',
    photo: null,
  });

  const [countryNames, setCountryNames] = useState([]);

  useEffect(() => {
    // Extract country names and codes
    const countryList = Object.entries(countries);
    const names = countryList.map(([code, details]) => details.name);

    setCountryNames(names);
  }, []);

  const handleChange = (e) => {
    const { name, value, type } = e.target;

    // Handle file input separately
    if (type === 'file') {
      setFormData({
        ...formData,
        [name]: e.target.files[0],
      });
    } else {
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Data:', formData);
    // You can add form submission logic here

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const validEmail = emailRegex.test(formData.email);
    const validConfirmEmail = emailRegex.test(formData.confirmEmail);

    if ((validEmail === false) && (validConfirmEmail === false)){
      alert('Please enter a valid email');
      return;
    } 
  };

  return (
    <div className='container'>
      <form onSubmit={handleSubmit} className='form-style'>
        <h1>Personal Information</h1>
        <div className="input-group">
          <div className="input-group-item">
            <label className='label-style'>
              First Name:
              <input
                type="text"
                name="firstName"
                value={formData.firstName}
                onChange={handleChange}
                className='input-style'
                placeholder='First Name'
                required
              />
            </label>
          </div>
          <div className="input-group-item">
            <label className='label-style'>
              Last Name:
              <input
                type="text"
                name="lastName"
                value={formData.lastName}
                onChange={handleChange}
                className='input-style'
                placeholder='Last Name'
              />
            </label>
          </div>
        </div>
        <br />
        <div className="photo-upload">
          <label className='label-style'>
            Photo:
            <input
              type="file"
              name="photo"
              accept="image/*"
              onChange={handleChange}
              className='input-style'
            />
          </label>
        </div>
        <br />
        <div className='input-group'>
          <div className='input-group-item'>
            <label className='label-style'>
              Email:
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                className='input-style'
                placeholder='Email'
                required
              />
            </label>
          </div>
          <div className="input-group-item">
            <label className='label-style'>
                Confirm Email:
                <input
                  type="confirmEmail"
                  name="confirmEmail"
                  value={formData.confirmEmail}
                  onChange={handleChange}
                  className='input-style'
                  placeholder='Confirm Email'
                  required
                />
              </label>
          </div>
        </div>
          <br />
          <label className='label-style'>
            Address(line 1):
            <input
              type="text"
              name="street"
              value={formData.street}
              onChange={handleChange}
              className='input-style'
              placeholder='Street/Locality'
            />
          </label>
          <br />
          <label className='label-style'>
            Address(line 2):
            <input
              type="text"
              name="city"
              value={formData.city}
              onChange={handleChange}
              className='input-style'
              placeholder='City/Town/Village'
            />
          </label>
          <br />
          <div className='input-group'>
            <div className='input-group-item'>
              <label className='label-style'>
                State:
                <input
                  type="text"
                  name="state"
                  value={formData.state}
                  onChange={handleChange}
                  className='input-style'
                  placeholder='State'
                />
              </label>
            </div>
            <div className='input-group-item'>
              <label className='label-style'>
                PIN:
                <input
                  type="text"
                  name="pin"
                  value={formData.pin}
                  onChange={handleChange}
                  className='input-style'
                  placeholder='PIN Code'
                  required
                />
              </label>
            </div>
          </div>
          <br />
          <label className='label-style'>
            Country:
            <select name="country" value={formData.country} onChange={handleChange} className='select-style' placeholder='Country'>
              {countryNames.map((name, index) => (
                <option key={index} value={name}>
                  {name}
                </option>
              ))}
            </select>
          </label>
          <br />
        <button type="submit" className='button-style'>Submit</button>
      </form>
    </div>
  );
};

export default PersonalInfoForm;
