// reducerGeneric.js
import { UPDATE_FORM_FIELD } from './ActionTypesGeneric';

const initialState = {
  forms: {},
};

const genericReducer = (state = initialState, action) => {
  switch (action.type) {
    case UPDATE_FORM_FIELD:
      const { formName, fieldName, value } = action.payload;
      return {
        ...state,
        forms: {
          ...state.forms,
          [formName]: {
            ...state.forms[formName],
            [fieldName]: value,
          },
        },
      };
    default:
      return state;
  }
};

export default genericReducer;
