import React,{ useState, useEffect } from 'react';
import Select from "react-select";
import CreatableSelect from 'react-select/creatable';

import skillsListConf from "../../Static/Configurations/skillsListConf";
import skillsList from '../../Static/Configurations/skillsListConf';

const SkillsForm = () => {
    const [ searchTerm, setSearchTerm ] = useState('')
    const [ options, setOptions ] = useState([])

    useEffect(() => {
        if (searchTerm.length >= 3){
            console.log("inside more than length 3");
            const filteredList = skillsList.filter(item => item.label.toLocaleLowerCase().includes(searchTerm.toLocaleLowerCase()));
            setOptions(filteredList);
        } else{
            setOptions([]);
        }
    },[searchTerm])

    const handleInputChange = (newValue) => {
        setSearchTerm(newValue);
        console.log(newValue);
    }

    return(
        <div>
            <Select
                options={options}
                value={searchTerm}
                onInputChange={handleInputChange}
                inputValue={searchTerm}
            />
            <CreatableSelect isClearable options={options} />;
        </div>
    )
}

export default SkillsForm;
