import React, { useState, useRef } from 'react';

import './test.css';

const Test = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const fileInputRef = useRef(null);

  const handleButtonClick = () => {
    // Trigger click on the hidden file input
    fileInputRef.current.click();
  };

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  return (
    <div>
      {/* Hidden file input */}
      <input
        type="file"
        ref={fileInputRef}
        accept="image/*"
        onChange={handleFileChange}
        style={{ display: 'none' }}
      />

      {/* Custom-styled button */}
      <button className="custom-file-button" onClick={handleButtonClick}>
        Choose File
      </button>

      {/* Display custom text when no file is chosen */}
      {!selectedFile && <p>No file chosen</p>}

      {/* You can display the name of the selected file or any other relevant information */}
      {selectedFile && <p>Selected file: {selectedFile.name}</p>}
    </div>
  );
};

export default Test;
