import React, { useState, useEffect } from 'react';

import salaried from "../../Static/salaried.jpg";
import selfEmployed from "../../Static/self-employment.jpg";
import student from "../../Static/student.jpg";

const Description = () => {
    const briedDescription = '';

    const lines = [
        'I am batman...',
        'I can take responsibility for a end to end task...',
        'I am dead inside..',

        // Add more lines as needed
      ];

      const [currentLineIndex, setCurrentLineIndex] = useState(0);
      const [currentText, setCurrentText] = useState('');

      useEffect(() => {
        let lineIndex = 0;
        let textIndex = 0;

        const typeEffect = setInterval(() => {
          setCurrentText((prevText) => {
            const currentLine = lines[lineIndex];
            const updatedText = currentLine.substring(0, textIndex + 1);

            if (textIndex === currentLine.length) {
              // If the entire line is typed, move to the next line
              lineIndex = (lineIndex + 1) % lines.length;
              textIndex = 0;
            } else {
              textIndex += 1;
            }

            return updatedText;
          });
        }, 100); // Adjust the typing speed as needed

        return () => clearInterval(typeEffect);
      }, []);

    return(
        <div className='container text-center'>
            <div className='row justify-content-md-start'>
                <div class="mb-3">
                    <label for="exampleFormControlTextarea1" class="form-label">Tell us briefly about yourself</label>
                    <textarea class="form-control" id="exampleFormControlTextarea1" rows="3"></textarea>
                </div>
                <div class="mb-3">
                    <label for="exampleFormControlInput1" class="form-label">One characteristic which makes you special.</label>
                    <input type="email" class="form-control" id="exampleFormControlInput1" placeholder={currentText}/>
                </div>
            </div>
            <div className='row justify-content-md-center'>
                <label>Are you salaried, self-employed or a student?</label>
                <div className='col'>
                    <div class="card">
                        <img src={salaried} class="card-img-top" alt="..."/>
                        <div class="card-body">
                            <p class="card-text">Salaried</p>
                        </div>
                    </div>
                </div>
                <div className='col'>
                    <div class="card">
                        <img src={selfEmployed} class="card-img-top" alt="..."/>
                        <div class="card-body">
                            <p class="card-text">Self-employed</p>
                        </div>
                    </div>
                </div>
                <div className='col'>
                    <div class="card">
                        <img src={student} class="card-img-top" alt="..."/>
                        <div class="card-body">
                            <p class="card-text">Student</p>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Description;
